import React from 'react'
import styled from 'styled-components'

import city from '../assets/city.jpg'

const BackgroundImage = styled.div`
  background-image: url(${city});
  background-size: 100% auto;
  background-repeat: no-repeat
`
const AboutSection = styled.section`
  height: 900px;
  display: flex;
  align-items: center;
  justify-content: flex-end
`
const AboutBox = styled.section`
  background: rgba(0,0,255,.2);
  height: 50%;
  width: 33%;
  margin: 

`


export default function About() {
  return (
    <BackgroundImage>
      <AboutSection>
        <AboutBox>
          <h2>About Me</h2>
          <p>I'm Ashlea Morgan, scientist and writer. As a doctoral candidate in Neurobiology & Behavior at Columbia University, I study how neural signaling drives our ability to regulate emotions and behave flexibly. As a writer, I compose articles on scientific research for both scientific and general audiences and integrate those stories into other topics involving what makes us who we are.</p>
        </AboutBox>
      </AboutSection>
    </BackgroundImage>
  )
}
