import React from 'react'
import { Link } from 'react-router-dom'
import styled from 'styled-components'

import outreachImg from '../assets/outreach.png'
import writingImg from '../assets/writing.png'
import researchImg from '../assets/research.png'


const WorkContainer = styled.div`
  display: flex;
  flex-direction: row;
`
const Work = styled.section`
  height: 500px;
  width: 33.33%;
  display: flex;
  align-items: flex-end;
`
const Box = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 50%;
  width: 100%;
  background: rgba(14,24,84,.35);
`
const Title = styled.h2`
  color: rgb(233,115,40);
`


export default function WhatIDo() {

  // background image styles
  const writing = {
    backgroundImage: `url(${writingImg})`,
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover'
  }
  const outreach = {
    backgroundImage: `url(${outreachImg})`,
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover'

  }
  const research = {
    backgroundImage: `url(${researchImg})`,
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover'

  }
  const iDoArr = [
    {
      text: 'writing',
      style: writing
    },
    {
      text: 'research',
      style: research
    },
    {
      text: 'outreach',
      style: outreach
    }
  ]
  const iDo = iDoArr.map(work => {
    const formatedText = work.text.charAt(0).toUpperCase.concat()
    return (
      <Work style={work.style}>

        <Box>

          <Title>{work.text}</Title>
          <Link to='/writing'>
            <button>Go to</button>
          </Link>

        </Box>

      </Work>
    )
  })

  return (
    <WorkContainer>
      {iDo}
    </WorkContainer>
  )
}
