import React from 'react'
import { Link } from 'react-router-dom'
import outreachImg from '../assets/outreach.png'
import writingImg from '../assets/writing.png'
import researchImg from '../assets/research.png'

// styles
const writing = {
  backgroundImage: `url(${writingImg})`,
  backgroundRepeat: 'no-repeat'
}
const outreach = {
  backgroundImage: `url(${outreachImg})`,
  backgroundRepeat: 'no-repeat'

}
const research = {
  backgroundImage: `url(${researchImg})`
}


export default function WhatIDo() {

  const iDoArr = [
    {
      text: 'writing',
      styled: writing
    },
    {
      text: 'research',
      styled: research
    },
    {
      text: 'outreach',
      styled: outreach
    }
  ]
  const iDo = iDoArr.map(work => {
    return (
      <section style={work.styled}>
        <div>
          <h3>{work.text}</h3>
          <Link to='/writing'>
            <button>Go to</button>
          </Link>
        </div>
      </section>
    )
  })

  return (
    <div>
      {iDo}
    </div>
  )
}
