import React from 'react'
import ashlea from '../assets/ashlea-contact.png'
import SocialMedias from './SocialMedias'
import styled from 'styled-components'

import { AboutTitle } from '../components/About'

const ContactSection = styled.div`
  display: flex;
  justify-content: space-between;
  background-color: rgb(233,115,40);
  font-family: 'Manrope', sans-serif;
  letter-spacing: 2px;
  font-weight: 700
`
const Form = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 50%;
`
const Title = styled(AboutTitle)`
  color: rgb(14,24,84);
  margin-bottom: 20px;
`
const Image = styled.img`
  width: 50%
`
const Input = styled.input`
  height: 60px;
  width: 66%;
  font-family: 'Manrope', sans-serif;
  font-size: 18px;
  letter-spacing: 2px;
  padding: 0 20px;
  margin: 8px 0;
  background: rgba(0,0,0,0);
  border: white solid 1px;
  color: white;

  &::placeholder{
    color: white
  }
`
const Message = styled.textarea`

`

export default function ContactMe() {
  return (
    <ContactSection>
      <Form action="">
        <Title>Contact Me</Title>
        {/* <SocialMedias /> */}
        <Input type="text" className='input' placeholder='Name'/>
        <Input type="text" className='input' placeholder='Email'/>
        <Input type="text" className='input' placeholder='Subject'/>
        <Message type="text" className='input' placeholder='Type your message here...'/>
      </Form>
      <Image src={ashlea} alt="" />
    </ContactSection>
  )
}
